<?php
require "bootstrap.php";

if(!isset($_GET["id"])){
  die("you must supply an id to update");
}

/*
 * Types
 * 1 = Text
 * 6 = Sketch
 */
$type = isset($_GET["type"]) ? $_GET["type"] : 1;

head("PUT: Ideas");

  $id = $_GET["id"];

  $data = array();
  #$data["x"] = rand(100, 1000);
  #$data["y"] = rand(100, 1000);
  $data["lock"] = 0;

  switch($type){
    default:
      die("Type not recognized");
    break;
    case 1:
      $data["data"] = "Updated Idea: ".time();
    break;
    case 6:
      $data["data"] = "data:image/png;base64,";
    break;
  }

put("ideas/".$id, $data);

foot();